mapper.update:
UPDATE freeboard SET
hit = hit+1
WHERE no =#{no}

mapper.select:
SELECT COUNT(*) 
FROM freeboardReply
WHERE bno=#{bno}

SELECT group_id,group_step,group_tab 
FROM freeboardReply
WHERE no=#{no}

mapper.update:
UPDATE freeboardReply SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE freeboardReply SET
depth=depth+1
WHERE no=#{no}

UPDATE freeboardReply SET
msg = #{msg}
WHERE no = #{no}

UPDATE freeboardReply SET
msg=#{msg}
WHERE no=#{no}

UPDATE freeboardReply SET
depth = depth-1
WHERE no = #{no}
