mapper.update:
Update theater Set
theater_total_seat=#{theater_total_seat}
Where theater_no=#{theater_no}

mapper.insert:
Update theater Set
date_no=#{date_no}
Where theater_no=#{theater_no}

mapper.select:
Select time_no
From dateInfo
Where date_no=#{date_no}

Select time
From timeInfo
Where time_no=#{time_no}

Select theater_total_seat
From theater
Where theater_no=#{theater_no};
